mapper.select:
SELECT ,P.MATCH_ID,P.PRIZE_NAME
FROM M_PROJECT_ACHIEVEMENT PA LEFT JOIN M_PRIZE P ON PA.PRIZE_ID = P.PRIZE_ID


			AND PROJECT_ACHIEVEMENT_ID like #{projectAchievementId}


			AND MODULE = #{module}



			AND PRJECT_NAME like #{prjectNameParam}



			AND DEPARTMENT like #{departmentParam}


			AND RESPONSIBILE_PERSON = #{responsibilePerson}


			AND TELEPHONE = #{telephone}



					AND INTRODUCT like #{introductParam}


					AND PA.DEPARTMENT_ID = #{departmentId}


            AND PA.PRIZE_ID = #{prizeId}


                AND PA.CREATOR = #{creator}


                AND PA.UPDATER = #{updater}


                AND PA.REMOVED = #{removed}


            AND P.MATCH_ID = #{prize.matchId}


	ORDER BY UPDATE_TIME DESC
